package %packageName%.view

import androidx.lifecycle.Observer
import org.koin.androidx.viewmodel.ext.android.viewModel

import com.adrena.basearchitect.core.base.BaseFragment

import com.adrena.basearchitect.core.extensions.Event
import com.adrena.basearchitect.core.extensions.observe
import com.adrena.basearchitect.core.extensions.toGone
import com.adrena.basearchitect.core.extensions.toVisible
import androidx.navigation.fragment.navArgs

import com.adrena.basearchitect.databinding.%screenName%%screenElement%Binding

import %packageName%.viewmodel.%screenName%ViewModel

class %screenName%%screenElement% : BaseFragment<%screenName%%screenElement%Binding>() {

    private val %screenNameLowerCase%ViewModel: %screenName%ViewModel by viewModel()
    //private val viewInputArgument: %screenName%%screenElement%Args by navArgs()

    override fun screenName() = "%screenName%"

    override fun setupObserversViewModel() {
        observe(%screenNameLowerCase%ViewModel.delegate.showProgress, this::showProgress)
        observe(%screenNameLowerCase%ViewModel.delegate.hideProgress, this::hideProgress)
        observe(%screenNameLowerCase%ViewModel.delegate.showNetworkError, this::showNetworkError)
        observe(%screenNameLowerCase%ViewModel.delegate.hideNetworkError, this::hideNetworkError)
        observe(%screenNameLowerCase%ViewModel.delegate.showUnknownError, this::showUnknownError)
        observe(%screenNameLowerCase%ViewModel.delegate.hideUnknownError, this::hideUnknownError)
    }

    override fun init() {
        //---- Initialize your ui here
    }

    //----- UI Methods
    private fun showProgress(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.progress.viewProgress.toVisible()
            }
        }
    }

    private fun hideProgress(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.progress.viewProgress.toGone()
            }
        }
    }

    private fun showNetworkError(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.notNetwork.root.toVisible()
            }
        }
    }

    private fun hideNetworkError(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.notNetwork.root.toGone()
            }
        }
    }

    private fun showUnknownError(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.genericError.root.toVisible()
            }
        }
    }

    private fun hideUnknownError(event: Event<Unit>) {
        event.getContentIfNotHandled().let {
            it?.apply {
                bindingView.genericError.root.toGone()
            }
        }
    }
}

package %packageName%.viewmodel

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import com.adrena.basearchitect.core.extensions.Event

class %screenName%VMDelegate {
    //region Generic actions
    //region Show progress view
    private val _showProgress = MutableLiveData<Event<Unit>>()
    val showProgress: LiveData<Event<Unit>> get() = _showProgress
    fun showProgressPostValue() {
        _showProgress.value = Event(Unit)
    }
    //endregion
    //region Hide progress view
    private val _hideProgress = MutableLiveData<Event<Unit>>()
    val hideProgress: LiveData<Event<Unit>> get() = _hideProgress
    fun hideProgressPostValue() {
        _hideProgress.value = Event(Unit)
    }
    //endregion
    //region Show Network error
    private val _showNetworkError = MutableLiveData<Event<Unit>>()
    val showNetworkError: LiveData<Event<Unit>> get() = _showNetworkError
    fun showNetworkErrorPostValue() {
        _showNetworkError.value = Event(Unit)
    }
    //endregion
    //region Hide Network error
    private val _hideNetworkError = MutableLiveData<Event<Unit>>()
    val hideNetworkError: LiveData<Event<Unit>> get() = _hideNetworkError
    fun hideNetworkErrorPostValue() {
        _hideNetworkError.value = Event(Unit)
    }
    //endregion
    //region Show Unknown error
    private val _showUnknownError = MutableLiveData<Event<Unit>>()
    val showUnknownError: LiveData<Event<Unit>> get() = _showUnknownError
    fun showUnknownErrorPostValue() {
        _showUnknownError.value = Event(Unit)
    }
    //endregion
    //region Hide Unknown error
    private val _hideUnknownError = MutableLiveData<Event<Unit>>()
    val hideUnknownError: LiveData<Event<Unit>> get() = _hideUnknownError
    fun hideUnknownErrorPostValue() {
        _hideUnknownError.value = Event(Unit)
    }
    //endregion
    //endregion
}

