package %packageName%.repository

import android.util.Log
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.flow.flow

import %packageName%.entity.%screenName%EntityResponse

import com.adrena.basearchitect.core.base.BaseRepository
import com.adrena.basearchitect.core.base.NetworkResponse
import com.adrena.basearchitect.core.base.Response
import %packageName%.service.I%screenName%Service
import %packageName%.entity.%screenName%EntityRequest
import kotlin.jvm.Throws

class %screenName%Repository(
    private val i%screenName%Service: I%screenName%Service
): I%screenName%Repository, BaseRepository() {

    @Throws(Exception::class)
    override suspend fun callGetSomeMethod(): Flow<Response<%screenName%EntityResponse>>  = flow {
        val responseNetwork = i%screenName%Service.getSomeMethod(%screenName%EntityRequest())
        val response = NetworkResponse(responseNetwork!!)

        if (response.exception != null) {
            emit(Response.Failure(response.exception!!))
        } else {
            emit(Response.Success(response.response!!))
        }
    }.catch {
        Log.e("callLogin", "CATCH")
    }

}

